                            onBlur={handleBlur}
                            value={values.contactPersonFirstname}
                          />
                      </CCol>
                    </CFormGroup>
                    <CFormGroup row>
                      <CCol md="4">
                          <CLabel htmlFor="hf-password">Contact person Last Name</CLabel>
                      </CCol>
                      <CCol xs="12" md="8">
                          <CInput 
