    super();
    this.state = {
      companyName: '',
      contactPersonFirstname: '',
      contactPersonLastname: '',
      contactPersonMobile: '',
      contactPersonMobilePrefix: '+971',
      contactPersonEmail: '',
      companyPhone: '',
      companyPhonePrefix: '+971',
      emirates: '',
      buildingNumber: '',
      street: '',
      address: '',
    }
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {

    if(nextProps.customer.response) {
      this.props.history.push({
        pathname: '../customer/list-customer',
        appState: {
          customer_create_status : true,
        }
      });
    }

    if(nextProps.customerErrors.errors) {
      this.setState({
        error: nextProps.customerErrors.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  // reset
  handleReset = (resetForm) => {
    if (window.confirm('Reset?')) {
      resetForm();
    }
  };

  onSubmit(e) {
    e.preventDefault();
    const formData = this.state;
    var pattern = new RegExp(/^[0-9\b]+$/);

    // Company Name
    if(formData.companyName === "") {
      NotificationManager.warning("Company name is required");
      return false;
    }

    // Mobile Number
    if (formData.contactPersonMobile === "") {
      NotificationManager.warning("Mobile number is required!");
      return false;
    }else if(!pattern.test(formData.contactPersonMobile)) {
      NotificationManager.warning("Please enter only number.");
      return false;
    }else if(formData.contactPersonMobile.length != 9) {
      NotificationManager.warning("Please enter valid phone number.");
      return false;
    }

    // Contact Email
    if(formData.contactPersonEmail === "") {
      NotificationManager.warning("Email is required!");
      return false;
    }

  }
