import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {
  CCreateElement,
  CSidebar,
  CSidebarBrand,
  CSidebarNav,
  CSidebarNavDivider,
  CSidebarNavTitle,
  CSidebarMinimizer,
  CSidebarNavDropdown,
  CSidebarNavItem,
} from '@coreui/react'

import {useEffect} from 'react'
import CIcon from '@coreui/icons-react'

// sidebar nav config
import navigation from './_nav'

// import $ from 'jquery';
// window.jquery = window.$ = $

const TheSidebar = () => {
  const dispatch = useDispatch()
  const show = useSelector(state => state.sidebarShow)

  useEffect(() => {
    $(".c-sidebar-nav-link").click(function(event) {
      if(event.target.parentElement.parentElement.classList.contains('c-sidebar-nav')) {
        if($(".c-sidebar-nav-dropdown").hasClass('c-show')) {
          $(".c-sidebar-nav-dropdown").removeClass('c-show')
        }
       }
    })
   },[$])

  return (
    <CSidebar
      show={show}
      onShowChange={(val) => dispatch({type: 'set', sidebarShow: val })}
    >
      <CSidebarBrand className="d-xs-down-none" to="/">
        <CIcon
          className=""
          name="logo-negative"
          height={100}
        />
        <CIcon
          className="c-sidebar-brand-minimized"
          name="sygnet"
          height={35}
        />
      </CSidebarBrand>
      <CSidebarNav>

        <CCreateElement
          items={navigation}
          components={{
            CSidebarNavDivider,
            CSidebarNavDropdown,
            CSidebarNavItem,
            CSidebarNavTitle
          }}
        />
      </CSidebarNav>
      <CSidebarMinimizer className="c-d-md-down-none"/>
    </CSidebar>
  )
}

export default React.memo(TheSidebar)
