import React, { Component } from 'react'
import {
  CButton,
  CCard,
  CCardBody,
  CCol,
  CForm,
  CFormGroup,
  CInput,
  CLabel,
  CRow,
  CSelect,
  CAlert,
  CInputFile
} from '@coreui/react'
import CIcon from '@coreui/icons-react'
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { createCustomer } from '../../modules/customer/actions';
import { NotificationContainer, NotificationManager } from 'react-notifications';
import 'react-notifications/lib/notifications.css';

import validator from 'validator';
import axios from "axios";

class AddCustomer extends Component {
  constructor() {
    super();
    this.state = {
      companyName: '',
      contactPersonFirstname: '',
      contactPersonLastname: '',
      contactPersonMobile: '',
      contactPersonMobilePrefix: '+971',
      contactPersonEmail: '',
      companyPhone: '',
      companyPhonePrefix: '+971',
      emirates: '0',
      buildingNumber: '',
      street: '',
      address: '',
      file: '',
      fileName: '',
    }
    this.onSubmit = this.onSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {

    if(nextProps.customer.response) {
      this.props.history.push({
        pathname: '../customer/list-customer',
        appState: {
          customer_create_status : true,
        }
      });
    }

    if(nextProps.customerErrors.errors) {
      this.setState({
        error: nextProps.customerErrors.errors
      });
    }
  }

  onChange = e => {
    this.setState({ [e.target.id]: e.target.value });
  };

  // reset
  handleReset = (resetForm) => {
    if (window.confirm('Reset?')) {
      resetForm();
    }
  };

  onSubmit = async (e)  => {
    e.preventDefault();
    const customerData = this.state;
    var pattern = new RegExp(/^[0-9\b]+$/);

    // Company Name
    if(customerData.companyName === "") {
      NotificationManager.warning("Company name is required");
      return false;
    }

    // Mobile Number
    if (customerData.contactPersonMobile === "") {
      NotificationManager.warning("Mobile number is required!");
      return false;
    }else if(!pattern.test(customerData.contactPersonMobile)) {
      NotificationManager.warning("Please enter only number.");
      return false;
    }else if(customerData.contactPersonMobile.length != 9) {
      NotificationManager.warning("Please enter valid phone number.");
      return false;
    }

    // Contact Email
    if(!validator.isEmail(customerData.contactPersonEmail)) {
      NotificationManager.warning("Enter valid email");
      return false;
    }

    const formData = new FormData();
    formData.append("companyName", customerData.companyName);
    formData.append("contactPersonFirstname", customerData.contactPersonFirstname);
    formData.append("contactPersonLastname", customerData.contactPersonLastname);
    formData.append("contactPersonMobile", customerData.contactPersonMobile);
    formData.append("contactPersonEmail", customerData);

    //var customer = {};
    // customer.companyName = formData.companyName;
    // customer.contactPersonFirstname = formData.contactPersonFirstname;
    // customer.contactPersonLastname = formData.contactPersonLastname;
    // customer.contactPersonMobile = formData.contactPersonMobile;
    // customer.contactPersonEmail = formData.contactPersonEmail;
    // customer.companyPhone = formData.companyPhone;
    // customer.emirates = formData.emirates;
    // customer.buildingNumber = formData.buildingNumber;
    // customer.street = formData.street;
    // customer.address = formData.address;
    //this.props.createCustomer(customer);

  }

  saveFile = (e) => {
    this.setState({
      file: e.target.files[0],
      fileName: e.target.files[0].name
    });
  }

  uploadFile = async (e) => {
    e.preventDefault();
    const formDatas = this.state;
    const formData = new FormData();
    formData.append("file", formDatas.file);
    formData.append("fileName", formDatas.fileName);
    //console.log(formData);
    try {
      axios.post('http://139.59.74.104:5000/api/customers/createCustomer', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        },
      }).then(res => {
        console.log(res);
      })
    } catch (ex) {
      console.log(ex);
    }
  };

  render() {
    const { error, success } = this.state;
    return (
      <CRow>
        <NotificationContainer/>
        <CCol xs="12" md="6" className="pfs-md-5 offset-3">
          <CCard>
            <CCardBody>
              <CForm className="form-horizontal" onSubmit={this.uploadFile}>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-email">Company Name</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="companyName" 
                        name="companyName"
                        value={this.state.companyName}
                        placeholder="Name of your company"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Contact person First Name</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="contactPersonFirstname" 
                        name="contactPersonFirstname"
                        value={this.state.contactPersonFirstname}
                        placeholder="First name of contact person"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Contact person Last Name</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="contactPersonLastname" 
                        name="contactPersonLastname"
                        value={this.state.contactPersonLastname}
                        placeholder="Last name of contact person"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Contact person Mobile</CLabel>
                  </CCol>
                  <CCol xs="12" md="2">
                      <CInput 
                        type="tel" 
                        id="contactPersonMobilePrefix" 
                        name="contactPersonMobilePrefix"
                        value={this.state.contactPersonMobilePrefix}
                        readOnly="readOnly"
                      />
                  </CCol>
                  <CCol xs="12" md="6">
                      <CInput 
                        type="tel" 
                        id="contactPersonMobile" 
                        name="contactPersonMobile"
                        value={this.state.contactPersonMobile}
                        placeholder="55 555 5555"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Contact person Email</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="contactPersonEmail" 
                        name="contactPersonEmail"
                        value={this.state.contactPersonEmail}
                        placeholder="Email ID of contact person"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Company Phone</CLabel>
                  </CCol>
                  <CCol xs="12" md="2">
                      <CInput 
                        type="tel" 
                        id="companyPhonePrefix" 
                        name="companyPhonePrefix"
                        value={this.state.companyPhonePrefix}
                        readOnly="readOnly"
                      />
                  </CCol>
                  <CCol xs="12" md="6">
                      <CInput 
                        type="tel" 
                        id="companyPhone" 
                        name="companyPhone"
                        value={this.state.companyPhone}
                        placeholder="66 666 6666"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Emirates</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CSelect name="emirates" id="emirates" value={this.state.emirates} placeholder="Emirates" onChange={this.onChange} >
                        <option value="0">Please select</option>
                        <option value="1">Abu Dhabi</option>
                        <option value="2">Dubai</option>
                        <option value="3">Sharjah</option>
                        <option value="4">Ajman</option>
                        <option value="5">Umm Al-Quwain</option>
                        <option value="6">Fujairah</option>
                      </CSelect>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Building Number</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="buildingNumber" 
                        name="buildingNumber"
                        value={this.state.buildingNumber}
                        placeholder="Building Number"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Street</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="street" 
                        name="street"
                        value={this.state.street}
                        placeholder="Street Name"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Adress</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInput 
                        type="text" 
                        id="address" 
                        name="address"
                        value={this.state.address}
                        placeholder="Address"
                        onChange={this.onChange}
                      />
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Upload trade license</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                    <CInputFile id="file-input" name="file-input"/>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                  <CCol md="4">
                      <CLabel htmlFor="hf-password">Upload profile image/  company logo</CLabel>
                  </CCol>
                  <CCol xs="12" md="8">
                      <CInputFile id="file-input" name="file-input" onChange={this.saveFile}/>
                  </CCol>
                </CFormGroup>
                <CFormGroup row>
                    <CCol xs="12" md="12" className="text-center">
                      <CButton type="submit" color="primary"><CIcon name="cil-save" /> Save</CButton>
                    </CCol>
                </CFormGroup>
              </CForm>
            </CCardBody>
          </CCard>
        </CCol>
      </CRow>
    )
  }
}

AddCustomer.propTypes = {
  customerErrors: PropTypes.object.isRequired,
  customer: PropTypes.object.isRequired
};
const mapStateToProps = state => ({
  customerErrors: state.customerErrors,
  customer: state.customer
});
export default connect(
  mapStateToProps,
  { createCustomer }
)(AddCustomer);
